<?php namespace Hasatbey\Tugiclient\Models;use Illuminate\Database\Eloquent\Model;class Contents extends Model {	protected $table = 'cms_contents';	protected $guarded  = array('id'); // create kullanmak iÃ§in    	public function translate(){		return $this->belongsTo('Tugicms\Models\Site\Translate','translate_id');	}		public function page(){		return $this->belongsTo('Tugicms\Models\Site\Pages','page_id');	}	public function module(){		return $this->belongsTo('Tugicms\Models\Site\Module','modules_id');	}}